<div>
    <!-- Simplicity is the consequence of refined emotions. - Jean D'Alembert -->
	<div class="container" style="border-style: dashed">    
        @if(intval($prediccion) === 1)
        	<div class="mt-2 d-block justify-content-center">
            	<img src="data:image/png;base64," alt="Bot imagen">
        	</div>
            <h4><span class="badge badge-danger">Bot</span></h4>
		@else
			<div class="mt-2 d-block justify-content-center">
            	<img src="data:image/png;base64," alt="Humano imagen" />
			</div>
			<h4><span class="badge badge-success">Humano</span></h4>
        @endif
	</div>
</div>